import 'package:flutter/material.dart';

import 'ChatModel.dart';
import 'chat.dart';


class InboxCard extends StatelessWidget {
  const InboxCard({
    super.key, required this.chatModel
  });

  final ChatModel chatModel;

  @override
  Widget build(BuildContext context) {
    return InkWell(
      onTap: () {
        Navigator.push(
              context,
              MaterialPageRoute(builder: (context) => const ChatApp()),
            );

      },
      child: Column(
        children: [
          ListTile(
            leading: const CircleAvatar(
              backgroundImage: NetworkImage('data:image/jpeg;base64,'),
              radius: 25,
            ),
            title: Text(
              chatModel.name,
              style: Theme.of(context).textTheme.bodyLarge,
            ),
            subtitle: Text(
              chatModel.message,
              style: Theme.of(context).textTheme.labelMedium,
            ),
            trailing: Text(chatModel.time, style: Theme.of(context).textTheme.labelLarge),
          ),
          const Divider(thickness: 1),
        ],
      ),
    );
  }
}
